import React, { useState } from 'react';
import './Board.css';
import Hexagon from './Hexagon';

function Board() {
  const [isDragging, setDragging] = useState(false);

  const handleDragStart = (event) => {
    event.dataTransfer.setData('text/plain', 'circle'); // Data to be transferred during drag
    setDragging(true);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData('text/plain');
    if (data === 'circle') {
      // Get the hexagon container where the circle is dropped
      const hexagonContainer = event.target.closest(Hexagon);
      if (hexagonContainer) {
        // Do something with the drop position, e.g., center the circle in the hexagon
        const circle = document.querySelector('.circle-overlay');
        const hexagonBounds = hexagonContainer.getBoundingClientRect();
        const hexagonCenterX = hexagonBounds.left + hexagonBounds.width;
        const hexagonCenterY = hexagonBounds.top + hexagonBounds.height;

        circle.style.left = hexagonCenterX - circle.clientWidth / 2 + 'px';
        circle.style.top = hexagonCenterY - circle.clientHeight / 2 + 'px';
      }
    }
  };

  return (
      <div>
        <div className="hex-row">
          <div 
            className={`circle-overlay ${isDragging ? 'dragging' : ''}`}
            draggable
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
          ></div>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop}/>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop}/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
        </div>
        <div className="hex-row even" onDragOver={handleDragOver} onDrop={handleDrop}>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
        </div>
        <div className="hex-row"> 
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
        </div>
        <div className="hex-row even">
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
        </div>
        <div className="hex-row">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
        <div className="hex-row even">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
        <div className="hex-row">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
        <div className="hex-row even">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
      </div>
  );
}

export default Board;
