import './App.css';
import React from 'react';
import Board from './Board';
import About from './About'; 
import ContactUs from './ContactUs';
import { puzzlesList } from './Puzzles';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

function getRandomInt(max) {
  return Math.floor(Math.random() * max);
}

function App() {
  const initialPuzzleNumber = getRandomInt(puzzlesList.length);
  const champions = puzzlesList[initialPuzzleNumber];
  const enemyChampions = champions[0];
  const userChampions = champions[1];

  return (
    <Router>
      <div className="App">
        <nav>
          <Link to="/">Board</Link>
          <Link to="/about">About</Link>  
          <Link to="/contact">Contact</Link> 
        </nav>

        <Routes>
          <Route path="/" element={<Board enemyChampionsList={enemyChampions} userChampionsList={userChampions} initialPuzzleNumber={initialPuzzleNumber} />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<ContactUs />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
