import logo from './logo.svg';
import './App.css';
import Board from './Board';
import Champion from './Champion';

const convertToPixels = (coordinate, leftOrTop) => {
  if (leftOrTop === 'left') {
    return (32.7 * (coordinate % 2)) + ((coordinate + 1) * 2.95) + (coordinate * 62.4) + 62.4 / 2;
  }
  if (leftOrTop === 'top') {
    return (coordinate * 20.3) + (coordinate * 36) + 36;
  }
}

function App() {
  const enemyChampions = [
    { startingPosition: { left: convertToPixels(0, 'left'), top: convertToPixels(0, 'top') }, currentPosition: { left: convertToPixels(0, 'left'), top: convertToPixels(0, 'top') }, image: "https://dotesports.com/wp-content/uploads/2023/11/Corki.png?w=640", type: "corki", starLevel: 1, headliner: false, items: [] },
    { startingPosition: { left: convertToPixels(1, 'left'), top: convertToPixels(1, 'top') }, currentPosition: { left: convertToPixels(1, 'left'), top: convertToPixels(1, 'top') }, image: "https://prod.api.assets.riotgames.com/public/v1/asset/lol/13.24.1/CHAMPION_SKIN/223020/SPLASH?width=1920&height=1080&format=auto&auto=webp", type: "tahm-kench", starLevel: 1, headliner: false, items: [] },
  ];

  const userChampions = [
    { startingPosition: { left: convertToPixels(4, 'left'), top: convertToPixels(4, 'top') }, currentPosition: { left: convertToPixels(4, 'left'), top: convertToPixels(4, 'top') }, image: "https://dotesports.com/wp-content/uploads/2023/11/Corki.png?w=640", type: "corki", starLevel: 1, headliner: false, items: [] },
    { startingPosition: { left: convertToPixels(6, 'left'), top: convertToPixels(7, 'top') }, currentPosition: { left: convertToPixels(6, 'left'), top: convertToPixels(7, 'top') }, image: "https://prod.api.assets.riotgames.com/public/v1/asset/lol/13.24.1/CHAMPION_SKIN/223020/SPLASH?width=1920&height=1080&format=auto&auto=webp", type: "tahm-kench", starLevel: 1, headliner: false, items: [] },
  ];

  return (
    <div className="App">
      <Board enemyChampionsList={enemyChampions} userChampionsList={userChampions} />
      <Champion startingPosition={{ left: '34.15px', top: '36px' }}/>
    </div>
  );
}

export default App;
