import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import Board from './Board';
import Champion from './Champion';
import ChampionsList from './ChampionsList';

const convertToPixels = (coordinate, leftOrTop, even) => {
  if (leftOrTop === 'left') {
    if (even) {
      return 459.75 + 32.7 + ((coordinate + 1) * 2.95) + (coordinate * 62.4) + 62.4 / 2;
    }
    return 459.75 + ((coordinate + 1) * 2.95) + (coordinate * 62.4) + 62.4 / 2;
  }
  if (leftOrTop === 'top') {
    return (coordinate * 20.3) + (coordinate * 36) + 36 + 173.11;
  }
}

function App() {
  const enemyChampions = ChampionsList([
    ['Corki', 1, false, []],
    ['Tahm Kench', 1, false, []]
  ], 'enemy', [[0,0], [2, 2]]);

  const userChampions = ChampionsList([
    ['Corki', 1, false, []],
    ['Tahm Kench', 2, false, []]
  ], 'user');

  return (
    <div className="App">
      <Board enemyChampionsList={enemyChampions} userChampionsList={userChampions} />
    </div>
  );
}

export default App;
