import React, { useState } from 'react';
import './Board.css';
import Hexagon from './Hexagon';

function Board() {
  const [isDragging, setDragging] = useState(false);
  const [circlePosition, setCirclePosition] = useState({ left: 9, top: 11 });

  const handleDragStart = (event) => {
    event.dataTransfer.setData('text/plain', 'circle'); // Data to be transferred during drag
    setDragging(true);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event, hexagonCoordinates) => {
    event.preventDefault();
    const data = event.dataTransfer.getData('text/plain');
    if (data === 'circle') {
      // Do something with the drop position, e.g., center the circle in the hexagon
      const circle = document.querySelector('.circle-overlay');
      const hexagonCenterX = (hexagonCoordinates.left * 62.4) + 62.4 / 2;
      const hexagonCenterY = (hexagonCoordinates.top * 36) + 36;

      setCirclePosition({
        left: hexagonCenterX - circle.clientWidth / 2,
        top: hexagonCenterY - circle.clientHeight / 2,
      });
    }
  };

  return (
      <div>
        <div className="hex-row">
          <div 
            className={`circle-overlay ${isDragging ? 'dragging' : ''}`}
            style={{ left: circlePosition.left + 'px', top: circlePosition.top + 'px' }}
            draggable
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
          ></div>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop} hexagonCoordinates={{ left: 0, top: 0 }}/>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop} hexagonCoordinates={{ left: 1, top: 0 }}/>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop} hexagonCoordinates={{ left: 2, top: 0 }}/>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop} hexagonCoordinates={{ left: 3, top: 0 }}/>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop} hexagonCoordinates={{ left: 4, top: 0 }}/>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop} hexagonCoordinates={{ left: 5, top: 0 }}/>
          <Hexagon player="enemy" onDragOver={handleDragOver} onDrop={handleDrop} hexagonCoordinates={{ left: 6, top: 0 }}/>
        </div>
        <div className="hex-row even">
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
        </div>
        <div className="hex-row"> 
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
        </div>
        <div className="hex-row even">
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
          <Hexagon player="enemy"/>
        </div>
        <div className="hex-row">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
        <div className="hex-row even">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
        <div className="hex-row">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
        <div className="hex-row even">
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
          <Hexagon player="user"/>
        </div>
      </div>
  );
}

export default Board;
