import { useDrag } from 'react-dnd';

const Champion = ({ image, position, onDragStart, onDragEnd, hexagonType }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'CHAMPION',
    item: { id: image.id },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={(node) => {
        drag(node);
        onDragStart();
      }}
      style={{
        width: '50px',
        height: '50px',
        borderRadius: '25px',
        backgroundImage: `url(${image.url})`,
        backgroundSize: 'cover',
        position: 'absolute',
        left: position.x,
        top: position.y,
      }}
    >
      {isDragging && hexagonType && (
        <div style={{ fontSize: '14px', color: 'white', textAlign: 'center' }}>
          Dropping on {hexagonType} hexagon
        </div>
      )}
    </div>
  );
};

export default Champion;
