import React, { useEffect, useRef } from 'react';

function drawHexagon(ctx, sideLength) {
  const angle = (Math.PI / 180) * 60;
  ctx.beginPath();
  for (let i = 0; i < 6; i++) {
    const x = Math.cos(angle * i) * sideLength;
    const y = Math.sin(angle * i) * sideLength;
    ctx.lineTo(x, y);
  }
  ctx.closePath();
}

function HexagonImage({ imageUrl, sideLength }) {
  const canvasRef = useRef();

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const image = new Image();
    image.onload = function () {
      canvas.width = image.width;
      canvas.height = image.height;

      ctx.save();
      drawHexagon(ctx, sideLength);
      ctx.clip();

      ctx.drawImage(image, -100, -100, canvas.width, canvas.height);

      ctx.restore();
    };

    image.src = imageUrl;
  }, [imageUrl, sideLength]);

  return <canvas ref={canvasRef} />;
}

export default HexagonImage;
