const items = {
  'B.F. Sword': { image: "https://rerollcdn.com/items/BFSword.png", type: "B.F. Sword", flatStats: { attackDamage: 10 }, percentStats: {}, description: "10 Attack Damage." },
  'Chain Vest': { image: "https://rerollcdn.com/items/ChainVest.png", type: "Chain Vest", flatStats: { armor: 20 }, percentStats: {}, description: "10 Attack Damage." },
  "Giant\'s Belt": { image: "https://rerollcdn.com/items/GiantsBelt.png", type: "Giant's Belt", flatStats: { heatlh: 150, originalHealth: 150 }, percentStats: {} },
  'Needlessly Large Rod': { image: "https://rerollcdn.com/items/NeedlesslyLargeRod.png", type: "Needlessly Large Rod", flatStats: { abilityPower: 10 }, percentStats: {} },
  'Negatron Cloak': { image: "https://rerollcdn.com/items/NegatronCloak.png", type: "Negatron Cloak", flatStats: { magicResist: 20 }, percentStats: {} },
  'Recurve Bow': { image: "https://rerollcdn.com/items/RecurveBow.png", type: "Recurve Bow", flatStats: {}, percentStats: { attackSpeed: 0.1 } },
  'Sparring Gloves': { image: "https://rerollcdn.com/items/SparringGloves.png", type: "Sparring Gloves", flatStats: { criticalChance: 0.2 }, percentStats: {} },
  'Tear of the Goddess': { image: "https://rerollcdn.com/items/TearoftheGoddess.png", type: "Tear of the Goddess", flatStats: { mana: 15 }, percentStats: {} },
  "Warmog\'s Armor": { image: "https://rerollcdn.com/items/WarmogsArmor.png", type: "Warmog's Armor", flatStats: { health: 800, originalHealth: 800 }, percentStats: {} },
  "Deathblade": { image: "https://rerollcdn.com/items/Deathblade.png", type: "Deathblade", flatStats: {}, percentStats: { attackDamage: 0.66 } },
}

function ItemsList(champion) {
  var newChampion = champion;
  const championItems = champion.items;

  if (championItems.length > 0) {
	for (var i = 0; i < championItems.length; i++) {
		for (const [key, value] of Object.entries(items[championItems[i]].flatStats)) {
			newChampion = { ...newChampion, [key]: newChampion[key] + value };
		}
		for (const [key, value] of Object.entries(items[championItems[i]].percentStats)) {
			newChampion = { ...newChampion, [key]: Math.round(newChampion[key]/(1-value)) };
		}
	}
  }

  return newChampion;
}

export default ItemsList;
